const express = require('express');
const app = express();
app.use(express.static('public'));
const mysql = require('mysql');

// mysqlへの接続
const connection = mysql.createConnection({
  host: 'localhost',
  user: 'root',
  password: '[設定したパスワード]',
  database: 'list_app'
});

// mysqlへの接続ができなかったときのエラー表示
connection.connect((err) => {
  if (err) {
    console.log('error connecting: ' + err.stack);
    return;
  }
  console.log('success');
});

// トップ画面を表示するルーティングを作成してください
app.get('/', (req, res) => {
  connection.query(
    'SELECT * FROM users',
    (error, results) => {
      console.log(results);
      res.render('top.ejs');
    }
  );
});

// indexページへのルーティング
app.get('/index', (req, res) => {
  res.render('index.ejs');
});

app.listen(3000, () => console.log("Node.js is listening to 3000PORT"));
